class MovieModel {
  final int id;
  final String title;
  final String? overview;
  final String? posterPath;

  MovieModel({
    required this.id,
    required this.title,
    this.overview,
    this.posterPath,
  });

  factory MovieModel.fromJson(Map<String, dynamic> json) {
    return MovieModel(
      id: json['id'],
      title: json['title'],
      overview: json['overview'],
      posterPath: json['poster_path'],
    );
  }
}

import 'package:hive/hive.dart';

part 'movie_model.g.dart';

@HiveType(typeId: 0)
class MovieModel extends HiveObject {
  @HiveField(0)
  final int id;
  @HiveField(1)
  final String title;
  @HiveField(2)
  final String? overview;
  @HiveField(3)
  final String? posterPath;

  MovieModel({
    required this.id,
    required this.title,
    this.overview,
    this.posterPath,
  });

  factory MovieModel.fromJson(Map<String, dynamic> json) {
    return MovieModel(
      id: json['id'],
      title: json['title'],
      overview: json['overview'],
      posterPath: json['poster_path'],
    );
  }
}

import 'package:hive/hive.dart';
import '../models/movie_model.dart';

class FavoritesStorage {
  static const String boxName = 'favorites';

  Box<MovieModel> get _box => Hive.box<MovieModel>(boxName);

  Future<void> addFavorite(MovieModel movie) async {
    // AynÄ± film eklenmesin
    if (!_box.values.any((m) => m.id == movie.id)) {
      await _box.add(movie);
    }
  }

  Future<void> removeFavorite(int movieId) async {
    final key = _box.keys.firstWhere(
      (k) => _box.get(k)?.id == movieId,
      orElse: () => null,
    );
    if (key != null) await _box.delete(key);
  }

  List<MovieModel> getFavorites() {
    return _box.values.toList();
  }

  bool isFavorite(int movieId) {
    return _box.values.any((m) => m.id == movieId);
  }
}

import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
